public static int parseRoman(final String romanNumber) {
    if (romanNumber == null || romanNumber.isEmpty()) {
        return 0;
    }

    int decimal = 0;
    int lastNumber = 0;
    final String romanNumeral = romanNumber.toUpperCase();
    // operate on upper case characters to allow lower case input
    for (int x = romanNumeral.length() - 1; x >= 0; x--) {
        final char convertToDecimal = romanNumeral.charAt(x);
        switch (convertToDecimal) {
            case 'M':
                decimal = processDecimal(1000, lastNumber, decimal);
                lastNumber = 1000;
                break;
            case 'D':
                decimal = processDecimal(500, lastNumber, decimal);
                lastNumber = 500;
                break;
            case 'C':
                decimal = processDecimal(100, lastNumber, decimal);
                lastNumber = 100;
                break;
            case 'L':
                decimal = processDecimal(50, lastNumber, decimal);
                lastNumber = 50;
                break;
            case 'X':
                decimal = processDecimal(10, lastNumber, decimal);
                lastNumber = 10;
                break;
            case 'V':
                decimal = processDecimal(5, lastNumber, decimal);
                lastNumber = 5;
                break;
            case 'I':
                decimal = processDecimal(1, lastNumber, decimal);
                lastNumber = 1;
                break;
            default:
                // invalid Roman numeral character
                return 0;
        }
    }
    return decimal;
}
